      JSON_OBJECT('name', 'minHeight', 'type', 'text', 'label', 'Minimum Height', 'default', '600px', 'placeholder', '600px, 80vh, etc.'),
      JSON_OBJECT('name', 'maxWidth', 'type', 'text', 'label', 'Content Max Width', 'default', '800px', 'placeholder', '800px, 1200px, etc.'),
