      JSON_OBJECT('name', 'paddingBottom', 'type', 'select', 'label', 'Bottom Padding', 'options', JSON_ARRAY(JSON_OBJECT('value', 'pb-8', 'label', 'Small (2rem)'), JSON_OBJECT('value', 'pb-16', 'label', 'Medium (4rem)'), JSON_OBJECT('value', 'pb-24', 'label', 'Large (6rem)'), JSON_OBJECT('value', 'pb-32', 'label', 'Extra Large (8rem)')), 'default', 'pb-20'),
      JSON_OBJECT('name', 'headingSize', 'type', 'select', 'label', 'Heading Size', 'options', JSON_ARRAY(JSON_OBJECT('value', 'text-2xl', 'label', 'Small (2xl)'), JSON_OBJECT('value', 'text-3xl', 'label', 'Medium (3xl)'), JSON_OBJECT('value', 'text-4xl', 'label', 'Large (4xl)'), JSON_OBJECT('value', 'text-5xl', 'label', 'Extra Large (5xl)'), JSON_OBJECT('value', 'text-6xl', 'label', 'Huge (6xl)')), 'default', 'text-4xl'),
      JSON_OBJECT('name', 'subheadingSize', 'type', 'select', 'label', 'Subheading Size', 'options', JSON_ARRAY(JSON_OBJECT('value', 'text-base', 'label', 'Base'), JSON_OBJECT('value', 'text-lg', 'label', 'Large'), JSON_OBJECT('value', 'text-xl', 'label', 'Extra Large'), JSON_OBJECT('value', 'text-2xl', 'label', '2XL')), 'default', 'text-xl'),
