  'export default function HeroBackgroundImage({ config }) {\n  const {\n    heading = \"Welcome to Our Platform\",\n    subheading = \"Build amazing things with our tools\",\n    backgroundImage = \"/defaults/hero-bg-1.jpg\",\n    backgroundPosition = \"center\",\n    backgroundSize = \"cover\",\n    ctaPrimaryText = \"Get Started\",\n    ctaPrimaryLink = \"/signup\",\n    ctaSecondaryText = \"\",\n    ctaSecondaryLink = \"\",\n    textColor = \"#ffffff\",\n    overlayColor = \"#000000\",\n    overlayOpacity = 0.5,\n    contentAlignment = \"center\",\n    contentVerticalAlign = \"center\",\n    minHeight = \"600px\",\n    maxWidth = \"800px\",\n    paddingTop = \"80px\",\n    paddingBottom = \"80px\",\n    headingSize = \"text-4xl\",\n    subheadingSize = \"text-xl\",\n    headingWeight = \"font-bold\",\n    ctaPrimaryStyle = \"solid\",\n    ctaPrimarySize = \"lg\",\n    ctaSecondaryStyle = \"outline\",\n    animation = \"fade-in\"\n  } = config;\n\n  const alignmentClass = {\n    left: \"items-start text-left\",\n    center: \"items-center text-center\",\n    right: \"items-end text-right\"\n  }[contentAlignment] || \"items-center text-center\";\n\n  const verticalAlignClass = {\n    top: \"justify-start\",\n    center: \"justify-center\",\n    bottom: \"justify-end\"\n  }[contentVerticalAlign] || \"justify-center\";\n\n  return (\n    <div\n      className={`relative w-full flex ${verticalAlignClass} ${alignmentClass}`}\n      style={{\n        minHeight,\n        backgroundImage: `url(${backgroundImage})`,\n        backgroundPosition,\n        backgroundSize,\n        backgroundRepeat: \"no-repeat\",\n        paddingTop,\n        paddingBottom\n      }}\n    >\n      <div\n        className=\"absolute inset-0\"\n        style={{\n          backgroundColor: overlayColor,\n          opacity: overlayOpacity\n        }}\n      />\n      <div className=\"relative z-10 w-full px-4 sm:px-6 lg:px-8\">\n        <div className=\"mx-auto\" style={{ maxWidth }}>\n          <h1 className={`${headingSize} ${headingWeight} mb-4 sm:mb-6`} style={{ color: textColor }}>\n            {heading}\n          </h1>\n          {subheading && (\n            <p className={`${subheadingSize} mb-8 sm:mb-10 opacity-90`} style={{ color: textColor }}>\n              {subheading}\n            </p>\n          )}\n          <div className={`flex flex-wrap gap-4 ${contentAlignment === \"center\" ? \"justify-center\" : contentAlignment === \"right\" ? \"justify-end\" : \"justify-start\"}`}>\n            {ctaPrimaryText && ctaPrimaryLink && (\n              <a\n                href={ctaPrimaryLink}\n                className={`inline-flex items-center justify-center px-6 py-3 text-base font-semibold rounded-lg transition-all duration-200 hover:opacity-90 ${ctaPrimaryStyle === \"solid\" ? \"bg-white text-gray-900\" : ctaPrimaryStyle === \"outline\" ? \"border-2 border-white text-white\" : \"text-white\"}`}\n              >\n                {ctaPrimaryText}\n              </a>\n            )}\n            {ctaSecondaryText && ctaSecondaryLink && (\n              <a\n                href={ctaSecondaryLink}\n                className={`inline-flex items-center justify-center px-6 py-3 text-base font-semibold rounded-lg border-2 transition-all duration-200 hover:bg-white hover:bg-opacity-10`}\n                style={{\n                  borderColor: textColor,\n                  color: textColor\n                }}\n              >\n                {ctaSecondaryText}\n              </a>\n            )}\n          </div>\n        </div>\n      </div>\n    </div>\n  );\n}',
  true,
  true,
  false,
  0,
  NOW(),
  NOW()
)
