  `description` = VALUES(`description`),
  `category` = VALUES(`category`),
  `defaultConfig` = VALUES(`defaultConfig`),
  `configSchema` = VALUES(`configSchema`),
  `componentCode` = VALUES(`componentCode`),
  `updatedAt` = NOW();

