CAST('{"fields":[{"name":"icon","type":"text","label":"Icon (emoji or text)","required":false,"default":"üí°"},{"name":"heading","type":"text","label":"Heading","required":true,"default":"Ready to transform your workflow?"},{"name":"description","type":"textarea","label":"Description","required":false,"default":"Join 10,000+ happy users"},{"name":"ctaText","type":"text","label":"CTA Button Text","required":true,"default":"Get Started Free"},{"name":"ctaLink","type":"text","label":"CTA Button Link","required":true,"default":"/signup"},{"name":"backgroundColor","type":"color","label":"Background Color","default":"#ffffff"},{"name":"cardBackgroundColor","type":"color","label":"Card Background Color","default":"#f9fafb"},{"name":"textColor","type":"color","label":"Text Color","default":"#111827"},{"name":"borderRadius","type":"select","label":"Border Radius","options":[{"value":"rounded-lg","label":"Medium"},{"value":"rounded-xl","label":"Large"},{"value":"rounded-2xl","label":"Extra Large"}],"default":"rounded-xl"},{"name":"shadow","type":"select","label":"Shadow","options":[{"value":"shadow-none","label":"None"},{"value":"shadow","label":"Small"},{"value":"shadow-lg","label":"Large"},{"value":"shadow-xl","label":"Extra Large"}],"default":"shadow-lg"},{"name":"padding","type":"select","label":"Vertical Padding","options":[{"value":"py-12","label":"Small"},{"value":"py-16","label":"Medium"},{"value":"py-24","label":"Large"}],"default":"py-16"},{"name":"iconSize","type":"select","label":"Icon Size","options":[{"value":"32px","label":"Small (32px)"},{"value":"48px","label":"Medium (48px)"},{"value":"64px","label":"Large (64px)"}],"default":"48px"}]}' AS JSON),
'export default function CTACard({ config }) { const { icon = "üí°", heading = "Ready to transform your workflow?", description = "Join 10,000+ happy users", ctaText = "Get Started Free", ctaLink = "/signup", backgroundColor = "#ffffff", cardBackgroundColor = "#f9fafb", textColor = "#111827", borderRadius = "rounded-xl", shadow = "shadow-lg", padding = "py-16", iconSize = "48px" } = config; return (<div className={`w-full ${padding}`} style={{ backgroundColor }}><div className="container mx-auto px-4"><div className={`max-w-2xl mx-auto p-8 ${borderRadius} ${shadow} text-center`} style={{ backgroundColor: cardBackgroundColor }}><div className="mb-4" style={{ fontSize: iconSize }}>{icon}</div><h2 className="text-3xl font-bold mb-4" style={{ color: textColor }}>{heading}</h2>{description && (<p className="text-lg mb-6" style={{ color: textColor }}>{description}</p>)}<a href={ctaLink} className="inline-block px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700">{ctaText}</a></div></div></div>); }',
true, true, false, 0, NOW(), NOW())
