INSERT INTO `BlockTemplate` (`id`, `name`, `slug`, `description`, `category`, `defaultConfig`, `configSchema`, `componentCode`, `isSystem`, `isActive`, `isPro`, `usageCount`, `createdAt`, `updatedAt`)
VALUES ('testimonials-carousel-001', 'Testimonials - Carousel', 'testimonials-carousel', 'Testimonials carousel with navigation and autoplay', 'TESTIMONIALS',
CAST('{"heading":"What Our Customers Say","testimonials":[{"quote":"This product changed everything for our business. Highly recommended!","author":"John Doe","role":"CEO","company":"Acme Corp","avatar":"/avatars/1.jpg","rating":5},{"quote":"Best investment we have made this year.","author":"Jane Smith","role":"CTO","company":"Tech Inc","avatar":"/avatars/2.jpg","rating":5}],"autoplay":true,"autoplayInterval":5000,"showRating":true,"showAvatar":true,"backgroundColor":"#ffffff","padding":"py-16","cardStyle":"default","borderRadius":"rounded-lg"}' AS JSON),
CAST('{"fields":[{"name":"heading","type":"text","label":"Heading","required":true,"default":"What Our Customers Say"},{"name":"testimonials","type":"array","label":"Testimonials","required":true,"default":[{"quote":"This product changed everything","author":"John Doe","role":"CEO","company":"Acme Corp","avatar":"/avatars/1.jpg","rating":5}]},{"name":"autoplay","type":"boolean","label":"Autoplay","default":true},{"name":"autoplayInterval","type":"number","label":"Autoplay Interval (ms)","default":5000,"min":1000,"max":10000,"step":500},{"name":"showRating","type":"boolean","label":"Show Rating Stars","default":true},{"name":"showAvatar","type":"boolean","label":"Show Avatar Images","default":true},{"name":"backgroundColor","type":"color","label":"Background Color","default":"#ffffff"},{"name":"padding","type":"select","label":"Vertical Padding","options":[{"value":"py-12","label":"Small"},{"value":"py-16","label":"Medium"},{"value":"py-24","label":"Large"}],"default":"py-16"},{"name":"cardStyle","type":"select","label":"Card Style","options":[{"value":"default","label":"Default"},{"value":"bordered","label":"Bordered"},{"value":"shadowed","label":"Shadowed"}],"default":"default"},{"name":"borderRadius","type":"select","label":"Border Radius","options":[{"value":"rounded","label":"Small"},{"value":"rounded-lg","label":"Medium"},{"value":"rounded-xl","label":"Large"}],"default":"rounded-lg"}]}' AS JSON),
'export default function TestimonialsCarousel({ config }) { const { heading = "What Our Customers Say", testimonials = [], autoplay = true, autoplayInterval = 5000, showRating = true, showAvatar = true, backgroundColor = "#ffffff", padding = "py-16", cardStyle = "default", borderRadius = "rounded-lg" } = config; return (<div className={`w-full ${padding}`} style={{ backgroundColor }}><div className="container mx-auto px-4"><div className="text-center mb-12"><h2 className="text-4xl font-bold mb-4">{heading}</h2></div><div className="max-w-4xl mx-auto"><div className="space-y-6">{testimonials.map((t, i) => (<div key={i} className={`p-6 ${borderRadius} ${cardStyle === "bordered" ? "border" : cardStyle === "shadowed" ? "shadow-lg" : ""} bg-white`}><div className="flex items-start gap-4">{showAvatar && t.avatar && (<img src={t.avatar} alt={t.author} className="w-12 h-12 rounded-full" />)}<div className="flex-1"><p className="text-lg mb-4 text-gray-700">"{t.quote}"</p><div className="flex items-center justify-between"><div><p className="font-semibold">{t.author}</p><p className="text-sm text-gray-600">{t.role} at {t.company}</p></div>{showRating && t.rating && (<div className="flex">{Array.from({ length: t.rating }).map((_, ri) => (<span key={ri}>‚≠ê</span>))}</div>)}</div></div></div></div>))}</div></div></div></div>); }',
true, true, false, 0, NOW(), NOW())
