INSERT INTO `BlockTemplate` (`id`, `name`, `slug`, `description`, `category`, `defaultConfig`, `configSchema`, `componentCode`, `isSystem`, `isActive`, `isPro`, `usageCount`, `createdAt`, `updatedAt`)
VALUES ('testimonials-grid-001', 'Testimonials - Grid', 'testimonials-grid', 'Testimonials displayed in a grid layout', 'TESTIMONIALS',
CAST('{"heading":"Customer Reviews","subheading":"See what our customers are saying","testimonials":[{"quote":"Amazing product!","author":"John Doe","role":"CEO","company":"Acme Corp","avatar":"/avatars/1.jpg","rating":5},{"quote":"Great service!","author":"Jane Smith","role":"CTO","company":"Tech Inc","avatar":"/avatars/2.jpg","rating":5},{"quote":"Highly recommended!","author":"Mike Johnson","role":"Founder","company":"Startup Co","avatar":"/avatars/3.jpg","rating":5}],"columns":3,"backgroundColor":"#f9fafb","padding":"py-16","cardStyle":"shadowed","showRating":true,"showAvatar":true,"avatarSize":"48px"}' AS JSON),
CAST('{"fields":[{"name":"heading","type":"text","label":"Heading","required":true,"default":"Customer Reviews"},{"name":"subheading","type":"textarea","label":"Subheading","required":false,"default":"See what our customers are saying"},{"name":"testimonials","type":"array","label":"Testimonials","required":true,"default":[{"quote":"Amazing product!","author":"John Doe","role":"CEO","company":"Acme Corp","avatar":"/avatars/1.jpg","rating":5}]},{"name":"columns","type":"select","label":"Number of Columns","options":[{"value":2,"label":"2 Columns"},{"value":3,"label":"3 Columns"},{"value":4,"label":"4 Columns"}],"default":3},{"name":"backgroundColor","type":"color","label":"Background Color","default":"#f9fafb"},{"name":"padding","type":"select","label":"Vertical Padding","options":[{"value":"py-12","label":"Small"},{"value":"py-16","label":"Medium"},{"value":"py-24","label":"Large"}],"default":"py-16"},{"name":"cardStyle","type":"select","label":"Card Style","options":[{"value":"default","label":"Default"},{"value":"bordered","label":"Bordered"},{"value":"shadowed","label":"Shadowed"}],"default":"shadowed"},{"name":"showRating","type":"boolean","label":"Show Rating Stars","default":true},{"name":"showAvatar","type":"boolean","label":"Show Avatar Images","default":true},{"name":"avatarSize","type":"select","label":"Avatar Size","options":[{"value":"32px","label":"Small (32px)"},{"value":"48px","label":"Medium (48px)"},{"value":"64px","label":"Large (64px)"}],"default":"48px"}]}' AS JSON),
'export default function TestimonialsGrid({ config }) { const { heading = "Customer Reviews", subheading = "See what our customers are saying", testimonials = [], columns = 3, backgroundColor = "#f9fafb", padding = "py-16", cardStyle = "shadowed", showRating = true, showAvatar = true, avatarSize = "48px" } = config; const gridCols = { 2: "sm:grid-cols-2", 3: "sm:grid-cols-2 lg:grid-cols-3", 4: "sm:grid-cols-2 lg:grid-cols-4" }[columns] || "sm:grid-cols-2 lg:grid-cols-3"; return (<div className={`w-full ${padding}`} style={{ backgroundColor }}><div className="container mx-auto px-4"><div className="text-center mb-12"><h2 className="text-4xl font-bold mb-4">{heading}</h2>{subheading && (<p className="text-lg text-gray-600">{subheading}</p>)}</div><div className={`grid grid-cols-1 ${gridCols} gap-6`}>{testimonials.map((t, i) => (<div key={i} className={`p-6 bg-white ${cardStyle === "bordered" ? "border" : cardStyle === "shadowed" ? "shadow-lg" : ""} rounded-lg`}><p className="text-gray-700 mb-4">"{t.quote}"</p><div className="flex items-center gap-3">{showAvatar && t.avatar && (<img src={t.avatar} alt={t.author} className="rounded-full" style={{ width: avatarSize, height: avatarSize }} />)}<div><p className="font-semibold">{t.author}</p><p className="text-sm text-gray-600">{t.role} at {t.company}</p></div></div>{showRating && t.rating && (<div className="mt-4 flex">{Array.from({ length: t.rating }).map((_, ri) => (<span key={ri}>‚≠ê</span>))}</div>)}</div>))}</div></div></div>); }',
true, true, false, 0, NOW(), NOW())
