INSERT INTO `BlockTemplate` (`id`, `name`, `slug`, `description`, `category`, `defaultConfig`, `configSchema`, `componentCode`, `isSystem`, `isActive`, `isPro`, `usageCount`, `createdAt`, `updatedAt`)
VALUES ('pricing-table-001', 'Pricing - 3-Tier', 'pricing-table', '3-tier pricing table with features and CTA buttons', 'PRICING',
CAST('{"heading":"Choose Your Plan","subheading":"Simple, transparent pricing","plans":[{"name":"Starter","price":"$9","period":"/month","features":["10 GB storage","1 user","Basic support"],"ctaText":"Start Free Trial","ctaLink":"/signup?plan=starter","highlighted":false},{"name":"Pro","price":"$29","period":"/month","badge":"POPULAR","features":["100 GB storage","5 users","Priority support","Advanced analytics"],"ctaText":"Start Free Trial","ctaLink":"/signup?plan=pro","highlighted":true},{"name":"Enterprise","price":"Custom","period":"","features":["Unlimited storage","Unlimited users","24/7 support","Custom integrations"],"ctaText":"Contact Sales","ctaLink":"/contact","highlighted":false}],"showAnnualToggle":true,"annualDiscount":"Save 20%","backgroundColor":"#ffffff","padding":"py-16","cardStyle":"bordered","highlightColor":"#3b82f6"}' AS JSON),
CAST('{"fields":[{"name":"heading","type":"text","label":"Heading","required":true,"default":"Choose Your Plan"},{"name":"subheading","type":"textarea","label":"Subheading","required":false,"default":"Simple, transparent pricing"},{"name":"plans","type":"array","label":"Pricing Plans","required":true,"default":[{"name":"Starter","price":"$9","period":"/month","features":["10 GB storage","1 user"],"ctaText":"Start Free Trial","ctaLink":"/signup","highlighted":false}]},{"name":"showAnnualToggle","type":"boolean","label":"Show Annual Toggle","default":true},{"name":"annualDiscount","type":"text","label":"Annual Discount Text","required":false,"default":"Save 20%"},{"name":"backgroundColor","type":"color","label":"Background Color","default":"#ffffff"},{"name":"padding","type":"select","label":"Vertical Padding","options":[{"value":"py-12","label":"Small"},{"value":"py-16","label":"Medium"},{"value":"py-24","label":"Large"}],"default":"py-16"},{"name":"cardStyle","type":"select","label":"Card Style","options":[{"value":"default","label":"Default"},{"value":"bordered","label":"Bordered"},{"value":"shadowed","label":"Shadowed"}],"default":"bordered"},{"name":"highlightColor","type":"color","label":"Highlight Color","default":"#3b82f6"}]}' AS JSON),
'export default function PricingTable({ config }) { const { heading = "Choose Your Plan", subheading = "Simple, transparent pricing", plans = [], showAnnualToggle = true, annualDiscount = "Save 20%", backgroundColor = "#ffffff", padding = "py-16", cardStyle = "bordered", highlightColor = "#3b82f6" } = config; return (<div className={`w-full ${padding}`} style={{ backgroundColor }}><div className="container mx-auto px-4"><div className="text-center mb-12"><h2 className="text-4xl font-bold mb-4">{heading}</h2>{subheading && (<p className="text-lg text-gray-600">{subheading}</p>)}</div><div className="grid grid-cols-1 md:grid-cols-3 gap-6 max-w-6xl mx-auto">{plans.map((p, i) => (<div key={i} className={`p-8 ${cardStyle === "bordered" ? "border-2" : cardStyle === "shadowed" ? "shadow-lg" : ""} rounded-lg ${p.highlighted ? "" : ""}`} style={p.highlighted ? { borderColor: highlightColor, borderWidth: "2px" } : {}}><div className="text-center mb-6"><h3 className="text-2xl font-bold mb-2">{p.name}</h3>{p.badge && (<span className="inline-block px-3 py-1 text-xs font-semibold rounded-full mb-2" style={{ backgroundColor: highlightColor, color: "#ffffff" }}>{p.badge}</span>)}<div className="text-4xl font-bold">{p.price}<span className="text-lg font-normal">{p.period}</span></div></div><ul className="mb-6 space-y-3">{p.features.map((f, fi) => (<li key={fi} className="flex items-center"><span className="mr-2">‚úì</span><span>{f}</span></li>))}</ul><a href={p.ctaLink} className={`block w-full text-center px-6 py-3 rounded-lg font-semibold ${p.highlighted ? "bg-blue-600 text-white" : "bg-gray-100 text-gray-900"} hover:opacity-90`}>{p.ctaText}</a></div>))}</div></div></div>); }',
true, true, false, 0, NOW(), NOW())
