INSERT INTO `BlockTemplate` (`id`, `name`, `slug`, `description`, `category`, `defaultConfig`, `configSchema`, `componentCode`, `isSystem`, `isActive`, `isPro`, `usageCount`, `createdAt`, `updatedAt`)
VALUES ('pricing-comparison-001', 'Pricing - Comparison Table', 'pricing-comparison', 'Feature comparison table with checkmarks across plans', 'PRICING',
CAST('{"heading":"Compare Plans","subheading":"See which plan is right for you","plans":["Starter","Pro","Enterprise"],"features":[{"name":"Storage","starter":"10 GB","pro":"100 GB","enterprise":"Unlimited"},{"name":"Users","starter":"1","pro":"5","enterprise":"Unlimited"},{"name":"Support","starter":"Basic","pro":"Priority","enterprise":"24/7"}],"backgroundColor":"#ffffff","padding":"py-16","tableStyle":"bordered","highlightColumn":"pro"}' AS JSON),
CAST('{"fields":[{"name":"heading","type":"text","label":"Heading","required":true,"default":"Compare Plans"},{"name":"subheading","type":"textarea","label":"Subheading","required":false,"default":"See which plan is right for you"},{"name":"plans","type":"array","label":"Plan Names","required":true,"default":["Starter","Pro","Enterprise"]},{"name":"features","type":"array","label":"Features","required":true,"default":[{"name":"Storage","starter":"10 GB","pro":"100 GB","enterprise":"Unlimited"}]},{"name":"backgroundColor","type":"color","label":"Background Color","default":"#ffffff"},{"name":"padding","type":"select","label":"Vertical Padding","options":[{"value":"py-12","label":"Small"},{"value":"py-16","label":"Medium"},{"value":"py-24","label":"Large"}],"default":"py-16"},{"name":"tableStyle","type":"select","label":"Table Style","options":[{"value":"default","label":"Default"},{"value":"bordered","label":"Bordered"},{"value":"striped","label":"Striped"}],"default":"bordered"},{"name":"highlightColumn","type":"select","label":"Highlight Column","options":[{"value":"none","label":"None"},{"value":"starter","label":"Starter"},{"value":"pro","label":"Pro"},{"value":"enterprise","label":"Enterprise"}],"default":"pro"}]}' AS JSON),
'export default function PricingComparison({ config }) { const { heading = "Compare Plans", subheading = "See which plan is right for you", plans = ["Starter", "Pro", "Enterprise"], features = [], backgroundColor = "#ffffff", padding = "py-16", tableStyle = "bordered", highlightColumn = "pro" } = config; return (<div className={`w-full ${padding}`} style={{ backgroundColor }}><div className="container mx-auto px-4"><div className="text-center mb-12"><h2 className="text-4xl font-bold mb-4">{heading}</h2>{subheading && (<p className="text-lg text-gray-600">{subheading}</p>)}</div><div className="overflow-x-auto"><table className={`w-full ${tableStyle === "bordered" ? "border" : tableStyle === "striped" ? "" : ""}`}><thead><tr className="bg-gray-50"><th className="px-4 py-3 text-left">Feature</th>{plans.map((p, i) => (<th key={i} className={`px-4 py-3 text-center ${highlightColumn === p.toLowerCase() ? "bg-blue-50" : ""}`}>{p}</th>))}</tr></thead><tbody>{features.map((f, fi) => (<tr key={fi} className={tableStyle === "striped" && fi % 2 === 0 ? "bg-gray-50" : ""}><td className="px-4 py-3 font-medium">{f.name}</td>{plans.map((p, pi) => (<td key={pi} className={`px-4 py-3 text-center ${highlightColumn === p.toLowerCase() ? "bg-blue-50" : ""}`}>{f[p.toLowerCase()] || "-"}</td>))}</tr>))}</tbody></table></div></div></div>); }',
true, true, false, 0, NOW(), NOW())
