INSERT INTO `BlockTemplate` (`id`, `name`, `slug`, `description`, `category`, `defaultConfig`, `configSchema`, `componentCode`, `isSystem`, `isActive`, `isPro`, `usageCount`, `createdAt`, `updatedAt`)
VALUES ('newsletter-signup-001', 'Newsletter Signup', 'newsletter-signup', 'Newsletter signup form with email capture', 'FORM',
CAST('{"heading":"Stay in the Loop","description":"Get weekly updates and exclusive offers","emailPlaceholder":"Enter your email","buttonText":"Subscribe","successMessage":"Thanks for subscribing!","backgroundColor":"#f9fafb","textColor":"#111827","formStyle":"inline","buttonStyle":"solid","buttonSize":"lg","padding":"py-16","borderRadius":"rounded-lg"}' AS JSON),
CAST('{"fields":[{"name":"heading","type":"text","label":"Heading","required":true,"default":"Stay in the Loop"},{"name":"description","type":"textarea","label":"Description","required":false,"default":"Get weekly updates and exclusive offers"},{"name":"emailPlaceholder","type":"text","label":"Email Placeholder","default":"Enter your email"},{"name":"buttonText","type":"text","label":"Button Text","required":true,"default":"Subscribe"},{"name":"successMessage","type":"text","label":"Success Message","required":false,"default":"Thanks for subscribing!"},{"name":"backgroundColor","type":"color","label":"Background Color","default":"#f9fafb"},{"name":"textColor","type":"color","label":"Text Color","default":"#111827"},{"name":"formStyle","type":"select","label":"Form Style","options":[{"value":"inline","label":"Inline"},{"value":"stacked","label":"Stacked"}],"default":"inline"},{"name":"buttonStyle","type":"select","label":"Button Style","options":[{"value":"solid","label":"Solid"},{"value":"outline","label":"Outline"}],"default":"solid"},{"name":"buttonSize","type":"select","label":"Button Size","options":[{"value":"sm","label":"Small"},{"value":"md","label":"Medium"},{"value":"lg","label":"Large"}],"default":"lg"},{"name":"padding","type":"select","label":"Vertical Padding","options":[{"value":"py-12","label":"Small"},{"value":"py-16","label":"Medium"},{"value":"py-24","label":"Large"}],"default":"py-16"}]}' AS JSON),
'export default function NewsletterSignup({ config }) { const { heading = "Stay in the Loop", description = "Get weekly updates and exclusive offers", emailPlaceholder = "Enter your email", buttonText = "Subscribe", successMessage = "Thanks for subscribing!", backgroundColor = "#f9fafb", textColor = "#111827", formStyle = "inline", buttonStyle = "solid", buttonSize = "lg", padding = "py-16", borderRadius = "rounded-lg" } = config; return (<div className={`w-full ${padding}`} style={{ backgroundColor }}><div className="container mx-auto px-4 max-w-2xl"><div className="text-center mb-8"><h2 className="text-3xl font-bold mb-4" style={{ color: textColor }}>{heading}</h2>{description && (<p className="text-lg mb-6" style={{ color: textColor }}>{description}</p>)}</div><form className={formStyle === "inline" ? "flex flex-col sm:flex-row gap-4" : "space-y-4"}><input type="email" placeholder={emailPlaceholder} className={`px-4 py-3 ${borderRadius} border flex-1`} required /><button type="submit" className={`px-6 py-3 ${borderRadius} font-semibold ${buttonStyle === "solid" ? "bg-blue-600 text-white" : "border-2 border-blue-600 text-blue-600"} hover:opacity-90`}>{buttonText}</button></form></div></div>); }',
true, true, false, 0, NOW(), NOW())
