INSERT INTO `BlockTemplate` (`id`, `name`, `slug`, `description`, `category`, `defaultConfig`, `configSchema`, `componentCode`, `isSystem`, `isActive`, `isPro`, `usageCount`, `createdAt`, `updatedAt`)
VALUES ('faq-accordion-001', 'FAQ - Accordion', 'faq-accordion', 'FAQ section with accordion-style expandable questions', 'FAQ',
CAST('{"heading":"Frequently Asked Questions","faqs":[{"question":"How does pricing work?","answer":"Our pricing is simple and transparent. Choose a plan that fits your needs and pay monthly or annually."},{"question":"What features are included?","answer":"All plans include our core features. Higher tiers unlock advanced capabilities."}],"defaultOpen":0,"allowMultipleOpen":false,"backgroundColor":"#ffffff","padding":"py-16","accordionStyle":"default","icon":"chevron","borderRadius":"rounded-lg"}' AS JSON),
CAST('{"fields":[{"name":"heading","type":"text","label":"Heading","required":true,"default":"Frequently Asked Questions"},{"name":"faqs","type":"array","label":"FAQs","required":true,"default":[{"question":"How does pricing work?","answer":"Our pricing is simple and transparent."}]},{"name":"defaultOpen","type":"number","label":"Default Open Index","default":0,"min":-1,"max":10},{"name":"allowMultipleOpen","type":"boolean","label":"Allow Multiple Open","default":false},{"name":"backgroundColor","type":"color","label":"Background Color","default":"#ffffff"},{"name":"padding","type":"select","label":"Vertical Padding","options":[{"value":"py-12","label":"Small"},{"value":"py-16","label":"Medium"},{"value":"py-24","label":"Large"}],"default":"py-16"},{"name":"accordionStyle","type":"select","label":"Accordion Style","options":[{"value":"default","label":"Default"},{"value":"bordered","label":"Bordered"},{"value":"shadowed","label":"Shadowed"}],"default":"default"},{"name":"icon","type":"select","label":"Icon Type","options":[{"value":"chevron","label":"Chevron"},{"value":"plus","label":"Plus/Minus"},{"value":"arrow","label":"Arrow"}],"default":"chevron"},{"name":"borderRadius","type":"select","label":"Border Radius","options":[{"value":"rounded","label":"Small"},{"value":"rounded-lg","label":"Medium"},{"value":"rounded-xl","label":"Large"}],"default":"rounded-lg"}]}' AS JSON),
'export default function FAQAccordion({ config }) { const { heading = "Frequently Asked Questions", faqs = [], defaultOpen = 0, allowMultipleOpen = false, backgroundColor = "#ffffff", padding = "py-16", accordionStyle = "default", icon = "chevron", borderRadius = "rounded-lg" } = config; const [openIndex, setOpenIndex] = React.useState(defaultOpen >= 0 ? defaultOpen : null); const toggle = (index) => { if (allowMultipleOpen) { setOpenIndex(openIndex === index ? null : index); } else { setOpenIndex(openIndex === index ? null : index); } }; return (<div className={`w-full ${padding}`} style={{ backgroundColor }}><div className="container mx-auto px-4 max-w-3xl"><div className="text-center mb-12"><h2 className="text-4xl font-bold mb-4">{heading}</h2></div><div className="space-y-4">{faqs.map((faq, i) => (<div key={i} className={`${accordionStyle === "bordered" ? "border" : accordionStyle === "shadowed" ? "shadow-lg" : ""} ${borderRadius} overflow-hidden`}><button onClick={() => toggle(i)} className="w-full px-6 py-4 flex items-center justify-between bg-gray-50 hover:bg-gray-100"><span className="font-semibold text-left">{faq.question}</span><span>{openIndex === i ? "‚àí" : "+"}</span></button>{openIndex === i && (<div className="px-6 py-4 bg-white"><p className="text-gray-700">{faq.answer}</p></div>)}</div>))}</div></div></div>); }',
true, true, false, 0, NOW(), NOW())
