customCSS:
/* Bold Theme - High Impact Design */
@import url('https://fonts.googleapis.com/css2?family=Poppins:wght@500;600;700;800&display=swap');
body { font-family: var(--theme-font-body); font-weight: 500; }
h1, h2, h3, h4, h5, h6 { font-family: var(--theme-font-heading); font-weight: 800; line-height: 1.1; letter-spacing: -0.02em; text-transform: uppercase; }
h1 { font-size: 4rem; }
h2 { font-size: 3rem; }
button, .btn { font-weight: 700; text-transform: uppercase; letter-spacing: 0.1em; box-shadow: 0 8px 16px rgba(220, 38, 38, 0.3); transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1); position: relative; overflow: hidden; }
button:hover, .btn:hover { transform: translateY(-3px); box-shadow: 0 12px 24px rgba(220, 38, 38, 0.4); }
button:active, .btn:active { transform: translateY(-1px); }
.product-card { border-radius: var(--theme-radius-lg); box-shadow: 0 8px 16px rgba(0, 0, 0, 0.1); transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1); overflow: hidden; }
.product-card:hover { box-shadow: 0 0 30px rgba(220, 38, 38, 0.5), 0 12px 24px rgba(0, 0, 0, 0.15); transform: translateY(-8px) scale(1.02); }
.header { box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1); }
.section { padding: var(--theme-section-spacing) 0; }
.badge, .tag { font-weight: 700; text-transform: uppercase; letter-spacing: 0.05em; padding: 0.5rem 1rem; border-radius: var(--theme-radius-full); }
