customCSS:
/* Classic Theme - Traditional & Elegant */
@import url('https://fonts.googleapis.com/css2?family=Playfair+Display:wght@400;500;600;700&family=Lora:wght@400;500;600;700&display=swap');
body { font-family: var(--theme-font-body); line-height: 1.7; }
h1, h2, h3, h4, h5, h6 { font-family: var(--theme-font-heading); font-weight: 600; letter-spacing: 0.02em; }
h1 { font-size: 2.75rem; line-height: 1.2; }
h2 { font-size: 2.25rem; line-height: 1.3; }
button, .btn { font-family: var(--theme-font-body); text-transform: none; letter-spacing: 0.05em; border: 2px solid var(--theme-button-primary); transition: all 0.2s ease; }
button:hover, .btn:hover { background: var(--theme-button-hover); border-color: var(--theme-button-hover); }
.product-card { border: 2px solid var(--theme-color-border); background: var(--theme-color-surface); padding: var(--theme-spacing-md); transition: all 0.3s ease; }
.product-card:hover { box-shadow: 0 10px 25px rgba(0, 0, 0, 0.1); transform: translateY(-4px); border-color: var(--theme-color-primary); }
.header { border-bottom: 3px solid var(--theme-color-border); }
.container { max-width: var(--theme-container-max-width); }
.section { margin-bottom: var(--theme-section-spacing); }
