customCSS:
/* Dark Theme - Sleek Dark Mode Design */
@import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600;700&display=swap');
body { font-family: var(--theme-font-body); background: var(--theme-color-background); color: var(--theme-color-text-primary); }
h1, h2, h3, h4, h5, h6 { font-family: var(--theme-font-heading); color: var(--theme-color-text-primary); }
button, .btn { background: var(--theme-button-primary); color: var(--theme-button-text); box-shadow: 0 4px 14px 0 rgba(96, 165, 250, 0.39); transition: all 0.3s ease; position: relative; overflow: hidden; }
button::before, .btn::before { content: ''; position: absolute; top: 0; left: -100%; width: 100%; height: 100%; background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent); transition: left 0.5s; }
button:hover::before, .btn:hover::before { left: 100%; }
button:hover, .btn:hover { background: var(--theme-button-hover); box-shadow: 0 6px 20px rgba(96, 165, 250, 0.5); transform: translateY(-2px); }
.product-card { background: var(--theme-color-surface); border: 1px solid var(--theme-color-border); border-radius: var(--theme-radius-lg); box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.3), 0 2px 4px -1px rgba(0, 0, 0, 0.2); transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1); overflow: hidden; }
.product-card:hover { box-shadow: 0 0 30px rgba(96, 165, 250, 0.4), 0 12px 24px rgba(0, 0, 0, 0.4); transform: translateY(-4px); border-color: var(--theme-color-primary); }
.header { background: var(--theme-header-bg) !important; backdrop-filter: blur(20px); border-bottom: 1px solid var(--theme-color-border); box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.3); }
.footer { background: var(--theme-footer-bg) !important; border-top: 1px solid var(--theme-color-border); }
.section { padding: var(--theme-section-spacing) 0; }
input, textarea, select { background: var(--theme-color-surface); border-color: var(--theme-color-border); color: var(--theme-color-text-primary); }
input:focus, textarea:focus, select:focus { border-color: var(--theme-color-primary); box-shadow: 0 0 0 3px rgba(96, 165, 250, 0.1); }
