customCSS:
/* Modern Theme - Vibrant & Gradient Design */
@import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600;700&display=swap');
body { font-family: var(--theme-font-body); }
h1, h2, h3, h4, h5, h6 { font-family: var(--theme-font-heading); font-weight: 700; }
button, .btn { background: linear-gradient(135deg, var(--theme-button-primary) 0%, var(--theme-button-hover) 100%); box-shadow: 0 4px 14px 0 rgba(var(--theme-button-primary), 0.39); transition: all 0.3s ease; }
button:hover, .btn:hover { transform: translateY(-2px); box-shadow: 0 6px 20px rgba(var(--theme-button-primary), 0.5); }
.product-card { border-radius: var(--theme-radius-lg); box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06); transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1); overflow: hidden; }
.product-card:hover { transform: scale(1.05); box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04); }
.header { backdrop-filter: blur(10px); background: rgba(255, 255, 255, 0.8); }
.section { padding: var(--theme-section-spacing) 0; }
